      //VERSION=3
function setup() {
   return {
      input: ["B04", "B08", "dataMask"],
      output: { bands: 4 }
   };
}

const ramp = [
   [-1, 0xC5142A],
   [0.05, 0xC5142A],
   [0.1, 0xC5142A],
   [0.15, 0xE02D2C],
   [0.2, 0xEF4C3A],
   [0.25, 0xFE6C4A],
   [0.3, 0xFF8D5A],
   [0.35, 0xFFAB69],
   [0.4, 0xFFC67D],
   [0.45, 0xFFE093],
   [0.5, 0xFFEFAB],
   [0.55, 0xFDFEC2],
   [0.6, 0xEAF7AC],
   [0.65, 0xD5EF94],
   [0.7, 0xB9E383],
   [0.75, 0x9BD873],
   [0.8, 0x77CA6F],
   [0.85, 0x53BD6B],
   [0.9, 0x14AA60],
   [0.95, 0x009755],
   [1, 0x007E47],
];

const visualizer = new ColorRampVisualizer(ramp);

function evaluatePixel(samples) {
   let ndvi = index(samples.B08, samples.B04);
   let imgVals = visualizer.process(ndvi);
   return imgVals.concat(samples.dataMask)
}
