// VERSION=3
const config2 = {
  default: {
    nbPastYears: 3,
    defaultOutputValue: -2,
    ndviMinValue: -1,
    currentIndexesMinValuesNumber: 1,
    pastIndexesMinValuesNumber: 3
  },
  ndviAnomaly: {
    pixelEvalMaxValue: 0.7
  },
};

function setup() {
  return {
    input: [{ bands: ["B04", "B08"] }],
    output: { bands: 3 },
    mosaicking: "ORBIT"
  };
}

function calculateNDVI(sample) {
  let denom = sample.B04 + sample.B08;
  if (denom === 0) return null;
  return (sample.B08 - sample.B04) / denom;
}

function calculateIndexesForSamples(samples, scenes, processSampleMethod) {
  const indexes = {};
  for (let i = 0; i < samples.length; i++) {
    let sample = samples[i];
    let indexValue = processSampleMethod(sample);
    if (indexValue === null) continue;
    let sceneYear = scenes[i].date.getFullYear();
    indexes[sceneYear] = indexes[sceneYear] || { sum: 0, count: 0 };
    indexes[sceneYear].sum += indexValue;
    indexes[sceneYear].count++;
  }
  return indexes;
}

function calculatePastIndexesAverage(indexes, currentYear, nbPastYears) {
  let pastSum = 0, pastCount = 0;
  for (let i = 1; i <= nbPastYears; i++) {
    let pastYearData = indexes[currentYear - i];
    if (pastYearData && pastYearData.count) {
      pastSum += pastYearData.sum / pastYearData.count;
      pastCount++;
    }
  }
  return pastCount >= config2.default.pastIndexesMinValuesNumber ? pastSum / pastCount : null;
}

function calculateIndexAverages(samples, scenes) {
  let indexes = calculateIndexesForSamples(samples, scenes, calculateNDVI);
  let currentYear = scenes[0].date.getFullYear();
  let currentAvg = indexes[currentYear] ? indexes[currentYear].sum / indexes[currentYear].count : null;
  let pastAvg = calculatePastIndexesAverage(indexes, currentYear, config2.default.nbPastYears);
  return { current: currentAvg, past: pastAvg };
}

function calculateNdviAnomaly(indexesAverages) {
  if (indexesAverages.current === null || indexesAverages.past === null) return config2.default.defaultOutputValue;
  return Math.max(Math.min(indexesAverages.current - indexesAverages.past, config2.ndviAnomaly.pixelEvalMaxValue), -config2.ndviAnomaly.pixelEvalMaxValue);
}

function evaluatePixel(samples, scenes) {
  let indexesAverages = calculateIndexAverages(samples, scenes);
  let anomaly = calculateNdviAnomaly(indexesAverages);
  return anomaly > 0 ? [0, 1, 0] : [1, 0, 0];  // Use green for positive and red for negative anomalies
}
